import { CommonModule } from '@angular/common';
import { Component, OnDestroy, OnInit, ViewChild } from '@angular/core';
import { ActivatedRoute } from '@angular/router';
import { GalleryItem, GalleryModule, ImageItem } from 'ng-gallery';
import { TabDirective, TabsModule, TabsetComponent } from 'ngx-bootstrap/tabs';
import { TimeagoModule } from 'ngx-timeago';
import { Member } from 'src/app/_models/member';
import { MemberMessagesComponent } from '../member-messages/member-messages.component';
import { MessageService } from 'src/app/_services/message.service';
import { Message } from 'src/app/_models/message';
import { PresenceService } from 'src/app/_services/presence.service';
import { AccountService } from 'src/app/_services/account.service';
import { User } from 'src/app/_models/user';
import { take } from 'rxjs';

@Component({
  selector: 'app-member-detail',
  standalone: true,
  templateUrl: './member-detail.component.html',
  styleUrls: ['./member-detail.component.css'],
  imports: [CommonModule, TabsModule, GalleryModule, TimeagoModule, MemberMessagesComponent]
})
export class MemberDetailComponent implements OnInit, OnDestroy {
  @ViewChild('memberTabs', { static: true }) memberTabs?: TabsetComponent;
  member: Member = {} as Member;
  images: GalleryItem[] = [];
  activeTab?: TabDirective;
  messages: Message[] = [];
  user?: User;

  constructor(public presenceService: PresenceService, private route: ActivatedRoute,
    private messageService: MessageService, private accountService: AccountService) {
    this.accountService.currentUser$.pipe(take(1)).subscribe({
      next: user => {
        if (user) this.user = user;
      }
    })
  }

  ngOnInit(): void {
    this.route.data.subscribe({
      next: data => this.member = data['member']
    })

    this.route.queryParams.subscribe({
      next: params => {
        params['tab'] && this.selectTab(params['tab'])
      }
    })

    this.getImages()
  }

  ngOnDestroy(): void {
    this.messageService.stopHubConnection();
  }

  loadMessages() {
    if (this.member)
      this.messageService.getMessageThread(this.member.username).subscribe({
        next: messages => this.messages = messages
      })
  }

  selectTab(heading: string) {
    if (this.memberTabs) {
      this.memberTabs.tabs.find(x => x.heading === heading)!.active = true;
    }
  }

  onTabActivated(data: TabDirective) {
    this.activeTab = data;
    if (this.activeTab.heading === 'Messages' && this.user) {
      this.messageService.createHubConnection(this.user, this.member.username);
    } else {
      this.messageService.stopHubConnection();
    }
  }

  getImages() {
    if (!this.member) return;
    for (const photo of this.member?.photos) {
      this.images.push(new ImageItem({ src: photo.url, thumb: photo.url }));
    }
  }

}
<div class="row">
  <div class="col-4">
      <div class="card">
          <img src="{{member.photoUrl || './assets/user.png'}}" alt="{{member.knownAs}}"
              class="card-img-top img-thumbnail">
          <div class="card-body">
              <div class="mb-2" *ngIf="(presenceService.onlineUsers$ | async)?.includes(member.username)">
                  <i class="fa fa-user-circle text-success"></i> Online now
              </div>
              <div>
                  <strong>Location:</strong>
                  <p>{{member.city}}, {{member.country}}</p>
              </div>
              <div>
                  <strong>Age:</strong>
                  <p>{{member.age}}</p>
              </div>
              <div>
                  <strong>Last active:</strong>
                  <p>{{member.lastActive | timeago}}</p>
              </div>
              <div>
                  <strong>Member since:</strong>
                  <p>{{member.created | date:'dd MMM yyyy'}}</p>
              </div>
          </div>
          <div class="card-footer">
              <div class="btn-group d-flex">
                  <button class="btn btn-primary">Like</button>
                  <button (click)="selectTab('Messages')" class="btn btn-success">Message</button>
              </div>
          </div>
      </div>
  </div>
  <div class="col-8">
      <tabset class="member-tabset" #memberTabs>
          <tab heading="About {{member.knownAs}}" (selectTab)="onTabActivated($event)">
              <h4>Description</h4>
              <p>{{member.introduction}}</p>
              <h4>Looking for</h4>
              <p>{{member.lookingFor}}</p>
          </tab>
          <tab heading="Interests" (selectTab)="onTabActivated($event)">
              <h4>Interests</h4>
              <p>{{member.interests}}</p>
          </tab>
          <tab heading="Photos" #photoTab="tab" (selectTab)="onTabActivated($event)">
              <gallery *ngIf="photoTab.active" [items]="images" class="gallery"></gallery>
          </tab>
          <tab heading="Messages" (selectTab)="onTabActivated($event)">
              <app-member-messages [username]="member.userName"></app-member-messages>
          </tab>
      </tabset>
  </div>
</div>
