import { CommonModule } from '@angular/common';
import { ChangeDetectionStrategy, Component, Input, OnInit, ViewChild } from '@angular/core';
import { FormsModule, NgForm } from '@angular/forms';
import { TimeagoModule } from 'ngx-timeago';
import { MessageService } from 'src/app/_services/message.service';

@Component({
  changeDetection: ChangeDetectionStrategy.OnPush,
  selector: 'app-member-messages',
  standalone: true,
  templateUrl: './member-messages.component.html',
  styleUrls: ['./member-messages.component.css'],
  imports: [CommonModule, TimeagoModule, FormsModule]
})
export class MemberMessagesComponent implements OnInit {
  @ViewChild('messageForm') messageForm?: NgForm;
  @Input() username?: string;
  messageContent = '';
  loading = false;

  constructor(public messageService: MessageService) { }

  ngOnInit(): void {
  }

  sendMessage() {
    if (!this.username) return;
    this.loading = true;
    this.messageService.sendMessage(this.username, this.messageContent).then(() => {
      this.messageForm?.reset();
    }).finally(() => this.loading = false);
  }

}
<div class="card">
  <div class="card-body"
      #scrollMe
      style="overflow: scroll; height: 500px;"
      [scrollTop]="scrollMe.scrollHeight"
  >
      <div *ngIf="(messageService.messageThread$ | async)?.length === 0">
          No messages yet... say hi by using the message box below
      </div>

      <ul
          *ngIf="(messageService.messageThread$ | async)!.length > 0" class="chat">
          <li *ngFor="let message of (messageService.messageThread$ | async)">
              <div>
                  <span class="chat-img float-end">
                      <img class="rounded-circle"
                          src="{{message.senderPhotoUrl || './assets/user.png'}}"
                          alt="image of user">
                  </span>
                  <div class="chat-body">
                      <div class="header">
                          <small class="text-muted">
                              <span class="fa fa-clock-o">{{message.messageSent | timeago}}</span>
                              <span class="text-danger" *ngIf="!message.dateRead
                                  && message.senderUsername !== username">(unread)</span>
                              <span class="text-success" *ngIf="message.dateRead
                                  && message.senderUsername !== username">(read {{message.dateRead | timeago}})</span>
                          </small>
                      </div>
                      <p>{{message.content}}</p>
                  </div>
              </div>
          </li>
      </ul>
  </div>

  <div class="card-footer">
      <form #messageForm="ngForm" (ngSubmit)="sendMessage()" autocomplete="off">
          <div class="input-group">
              <input
                  name="messageContent"
                  required
                  [(ngModel)]="messageContent"
                  type="text"
                  class="form-control input-sm"
                  placeholder="Send a private message">
              <div class="input-group-append">
                  <button [disabled]="!messageForm.valid || loading" class="btn btn-primary" type="submit">
                      Send <i *ngIf="loading" class="fa fa-spinner fa-spin"></i>
                  </button>
              </div>
          </div>
      </form>
  </div>
</div>
