import { Component } from '@angular/core';
import { Message } from '../_models/message';
import { Pagination } from '../_models/pagination';
import { MessageService } from '../_services/message.service';

@Component({
  selector: 'app-messages',
  templateUrl: './messages.component.html',
  styleUrls: ['./messages.component.css']
})
export class MessagesComponent {
  messages?: Message[] = [];
  pagination?: Pagination;
  container = "Unread";
  pageNumber = 1;
  pageSize = 5;
  loading = false;

  constructor(private messageService: MessageService) { }

  ngOnInit(): void {
    this.loadMessages();
  }

  loadMessages() {
    this.loading = true;
    this.messageService.getMessages(this.pageNumber, this.pageSize, this.container).subscribe({
      next: response => {
        this.messages = response.result;
        this.pagination = response.pagination;
        this.loading = false;
      }
    })
  }

  deleteMessage(id: number) {
    this.messageService.deleteMessage(id).subscribe({
      next: _ => this.messages?.splice(this.messages.findIndex(m => m.id === id), 1)
    })
  }

  pageChanged(event: any) {
    if (this.pageNumber !== event.page) {
      this.pageNumber = event.page;
      this.loadMessages();
    }
  }
}
<div class="mb-4 d-flex">
  <div class="btn-group" name="container">
      <button class="btn btn-primary" btnRadio="Unread"
          [(ngModel)]="container" (click)="loadMessages()">
          <i class="fa fa-envelope"></i> Unread
      </button>
      <button class="btn btn-primary" btnRadio="Inbox" [(ngModel)]="container"
          (click)="loadMessages()">
          <i class="fa fa-envelope-open"></i> Inbox
      </button>
      <button class="btn btn-primary" btnRadio="Outbox"
          [(ngModel)]="container" (click)="loadMessages()">
          <i class="fa fa-paper-plane"></i> Outbox
      </button>
  </div>
</div>

<div class="row" *ngIf="!messages || messages?.length === 0">
  <h3>No messages</h3>
</div>

<div class="row" *ngIf="messages && messages?.length !== 0">
  <table class="table table-hover" style="cursor: pointer">
      <thead>
          <tr>
              <th style="width: 40%">Message</th>
              <th style="width: 20%">From / To</th>
              <th style="width: 20%">Sent / Received</th>
              <th style="width: 20%"></th>
          </tr>
      </thead>
      <tbody class="align-middle">
          <tr [hidden]="loading" *ngFor="let message of messages"
              routerLink="{{container === 'Outbox'
              ? '/members/' + message.recipientUsername
              : '/members/' + message.senderUsername}}" [queryParams]="{tab: 'Messages'}">
              <td>{{message.content}}</td>
              <td>
                  <div *ngIf="container === 'Outbox'">
                      <img
                          class="img-circle rounded-circle me-2"
                          src="{{message.recipientPhotoUrl || './assets/user.png'}}"
                          alt="recipient photo">
                      <strong>{{message.recipientUsername |
                          titlecase}}</strong>
                  </div>
                  <div *ngIf="container !== 'Outbox'">
                      <img
                          class="img-circle rounded-circle me-2"
                          src="{{message.senderPhotoUrl || './assets/user.png'}}"
                          alt="recipient photo">
                      <strong>{{message.senderUsername | titlecase}}</strong>
                  </div>
              </td>
              <td>{{message.messageSent | timeago}}</td>
              <td>
                  <button
                      (click)="$event.stopPropagation()"
                      (click)="deleteMessage(message.id)"
                      class="btn btn-danger">Delete</button>
              </td>
          </tr>
      </tbody>

  </table>
</div>

<div class="d-flex justify-content-center" *ngIf="!loading && pagination && messages && messages.length > 0">
  <pagination
      [boundaryLinks]="true"
      [totalItems]="pagination.totalItems"
      [itemsPerPage]="pagination.itemsPerPage"
      [(ngModel)]="pagination.currentPage"
      (pageChanged)="pageChanged($event)"
      [maxSize]="10"
      previousText="&lsaquo;"
      nextText="&rsaquo;"
      firstText="&laquo;"
      lastText="&raquo;">
  </pagination>
</div>
